    return(
        <Fragment>
            <h2>Crear Cita</h2>
            
            <form
                onSubmit={submitCita}
            >
                <label>Nombre Mascota</label>
                <input 
                    type="text" 
                    name="mascota" 
                    className="u-full-width" 
                    placeholder="Nombre mascota"
                    onChange={actualizarState}
                    value={mascota}/>

                <label>Nombre propietario</label>
                <input 
                    type="text" 
                    name="propietario" 
                    className="u-full-width" 
                    placeholder="Nombre propietario" 
                    onChange={actualizarState}
                    value={propietario}/>
            
                <label>Fecha</label>
                <input 
                    type="date" 
                    name="fecha" 
                    className="u-full-width" 
                    onChange={actualizarState}
                    value={fecha}/>

                <label>Hora</label>
                <input 
                    type="time" 
                    name="hora" 
                    className="u-full-width" 
                    onChange={actualizarState}
                    value={hora}/>

