                <textarea
                    className="u-full-width"
                    name="sintomas" 
                    onChange={actualizarState}
                    value={sintomas}></textarea>
                
                <button type="submit" className="u-full-width button-primary">Agregar Cita</button>
            </form>
        </Fragment>
    );
}

export default Formulario;
